mapper.select:
select count(*)
from fb_member
where id=#{id}

select *
from fb_member
where id = #{id} and pw = #{pw}

select id
from fb_member
where member_name=#{member_name} and mail=#{mail}

select pw
from fb_member
where id=#{id} and member_name=#{member_name} and mail=#{mail}

mapper.update:
update fb_member
set
pw=#{pw}
where id=#{id} and member_name=#{member_name} and mail=#{mail}

mapper.delete:
delete 
from fb_member
where id=#{id} and pw=#{pw}

mapper.select:
select count(*)
from fb_lately_product
where member_no = #{member_no} and foods_no = #{foods_no}
